mapper.select:
SELECT  dlvr.dlvryStateCd,
        COUNT(*) AS dlvryCnt
  FROM  tb_deliveries AS dlvr
 WHERE  1=1

   AND  dlvr.stoBrcofcId = #{stoBrcofcId}


   AND  dlvr.riderBrcofcId = #{riderBrcofcId}

   AND  date_format(dlvr.dlvryRecvDt, "%Y%m%d%H%i%s")  >=  #{dlvryRecvDtStd}
   AND  date_format(dlvr.dlvryRecvDt, "%Y%m%d%H%i%s")  <=  #{dlvryRecvDtEnd}
 GROUP
    BY  dlvr.dlvryStateCd

SELECT  IFNUlL( COUNT(*), 0 ) AS dlvryCnt
  FROM  tb_deliveries
 WHERE  dlvryNo = #{dlvryNo}

mapper.update:
UPDATE  tb_deliveries
   SET  dlvryStateCd = '02',
        dlvryDsptcDt = date_format(now(), "%Y%m%d%H%i%s"),
        riderId = #{riderId},
        riderBrcofcId = #{riderBrcofcId}
 WHERE  dlvryNo = #{dlvryNo}

UPDATE  tb_deliveries
   SET  dlvryStateCd = '05'
 WHERE  dlvryNo = #{dlvryNo}

mapper.select:
SELECT store.stoId, store.stoBsnsRgnmb, store.stoMtlty, store.stoBizSeCd, store.stoRprsntvNm, store.stoBrdYmd, store.stoCrprtRgnmb, store.stoOpnngYmd, store.stoBsnsPlaceAdres, store.stoHdofcAdres, store.stoBizcnd, store.stoInduty, store.stoTelno, store.stoVrtlBankCd, IFNULL(( SELECT bank.bankNm FROM tb_banks AS bank WHERE bank.bankCd = store.stoVrtlBankCd ),'') AS stoVrtlBankNm, store.stoVrtlAcnt, store.stoSetSeCd, store.stoVatYn, store.stoNightSrchrApplyYn, store.stoNightSrchrStdTm, store.stoNightSrchrEndTm, store.stoNightSrchrAmnt, store.stoLa, store.stoLo, store.stoStateCd FROM tb_stores AS store WHERE store.brcofcId = #{brcofcId}
AND store.stoMtlty LIKE CONCAT('%', #{stoMtlty} ,'%')
ORDER BY store.stoId

SELECT  IFNUlL( COUNT(*), 0 ) AS stoId
  FROM  tb_stores
 WHERE  1 = 1

   AND  stoId = #{stoId}


   AND  stoBsnsRgnmb = #{stoBsnsRgnmb}

mapper.update:
UPDATE tb_stores
stoBsnsRgnmb = #{stoBsnsRgnmb}, stoMtlty = #{stoMtlty}, stoRprsntvNm = #{stoRprsntvNm}, stoBrdYmd = #{stoBrdYmd}, stoCrprtRgnmb = #{stoCrprtRgnmb}, stoOpnngYmd = #{stoOpnngYmd}, stoBsnsPlaceAdres = #{stoBsnsPlaceAdres}, stoHdofcAdres = #{stoHdofcAdres}, stoBizcnd = #{stoBizcnd}, stoInduty = #{stoInduty}, stoTelno = #{stoTelno}, stoVrtlBankCd = #{stoVrtlBankCd}, stoVrtlAcnt = #{stoVrtlAcnt}, stoStateCd = #{stoStateCd}, stoVatYn = #{stoVatYn}, stoBizSeCd = #{stoBizSeCd}
WHERE stoId = #{stoId}

UPDATE  tb_stores

        stoNightSrchrApplyYn = #{stoNightSrchrApplyYn},
        stoNightSrchrStdTm = #{stoNightSrchrStdTm},
        stoNightSrchrEndTm = #{stoNightSrchrEndTm},
        stoNightSrchrAmnt = #{stoNightSrchrAmnt}

 WHERE  stoId = #{stoId}

UPDATE  tb_store_surcharges

        srchrCn = #{srchrCn},
        srchrAmnt = #{srchrAmnt},
        srchrApplyYn = #{srchrApplyYn}

 WHERE  stoId = #{stoId}
   AND  srchrSeCd = #{srchrSeCd}

mapper.delete:
DELETE
  FROM  tb_store_area_settings
 WHERE  stoId = #{stoId}
   AND  setSeqNo = #{setSeqNo}

mapper.update:
UPDATE  tb_store_distance_settings

        setStdDstnc = #{setStdDstnc},
        setEndDstnc = #{setEndDstnc},
        setAmnt = #{setAmnt}

 WHERE  stoId = #{stoId}
   AND  setSeqNo = #{setSeqNo}

mapper.delete:
DELETE
  FROM  tb_store_distance_settings
 WHERE  stoId = #{stoId}
   AND  setSeqNo = #{setSeqNo}

mapper.update:
UPDATE  tb_store_special_settings

        setNm = #{setNm},
        setAmnt = #{setAmnt},

 WHERE  stoId = #{stoId}
   AND  setSeqNo = #{setSeqNo}

mapper.select:
SELECT rider.riderId, rider.brcofcId, rider.riderCelno, rider.riderNm, rider.riderWthdrBankCd, IFNULL(( SELECT bank.bankNm FROM tb_banks AS bank WHERE bank.bankCd = rider.riderWthdrBankCd ),'') AS riderWthdrBankNm, rider.riderWthdrAcnt, rider.riderLoginYn, rider.riderMinWthdrAmnt, rider.riderCallLimit, rider.riderCallDelayTime, rider.riderStateCd FROM tb_riders AS rider WHERE rider.brcofcId = #{brcofcId}
AND rider.riderNm LIKE CONCAT('%', #{riderNm} ,'%')
ORDER BY rider.riderId

SELECT  IFNUlL( COUNT(*), 0 ) AS riderId
  FROM  tb_riders
 WHERE  1 = 1

   AND  riderId = #{riderId}


   AND  riderCelno = #{riderCelno}

mapper.update:
UPDATE tb_riders
riderNm = #{riderNm}, riderCelno = #{riderCelno},
riderWthdrBankCd = #{riderWthdrBankCd},
riderWthdrAcnt = #{riderWthdrAcnt},
riderMinWthdrAmnt = #{riderMinWthdrAmnt}, riderCallLimit = #{riderCallLimit}, riderCallDelayTime = #{riderCallDelayTime}, riderStateCd = #{riderStateCd}
WHERE riderId = #{riderId}

mapper.select:
SELECT  if( pointSeCd = '01', pointAmnt, 0 ) as pointIncrsAmnt,
        if( pointSeCd = '02', pointAmnt, 0 ) as pointDecrsAmnt,
        date_format(pointRegDt, "%Y%m%d%H%i%s") pointRegDt,
        pointNote
  FROM  tb_branch_points
 WHERE  brcofcId = #{brcofcId}
   AND  date_format(pointRegDt, "%Y%m%d")  >=  #{stdDate}
   AND  date_format(pointRegDt, "%Y%m%d")  <=  #{endDate}
 ORDER
    BY  pointRegDt desc

SELECT store.stoId, store.stoMtlty, store.stoRprsntvNm, store.stoTelno, IFNULL( SUM(CASE when pointSeCd = '01' THEN pointAmnt when pointSeCd = '02' THEN pointAmnt * -1 ELSE 0 END ),0) AS stoPoint FROM tb_stores AS store LEFT OUTER JOIN tb_store_points AS point ON store.stoId = point.stoId WHERE store.brcofcId = #{brcofcId}
AND store.stoId = #{stoId}
GROUP BY stoId

SELECT rider.riderId, rider.riderNm, rider.riderCelno, rider.riderWthdrBankCd, IFNULL(( SELECT bank.bankNm FROM tb_banks AS bank WHERE bank.bankCd = rider.riderWthdrBankCd ),'') AS riderWthdrBankNm, IFNULL( rider.riderWthdrAcnt,'') AS riderWthdrAcnt, rider.riderMinWthdrAmnt, IFNULL( SUM(CASE when pointSeCd = '01' THEN pointAmnt when pointSeCd = '02' THEN pointAmnt * -1 ELSE 0 END ),0) AS riderPoint FROM tb_riders AS rider LEFT OUTER JOIN tb_rider_points AS point ON rider.riderId = point.riderId WHERE rider.brcofcId = #{brcofcId}
AND rider.riderId = #{riderId}
GROUP BY riderId
