mapper.select:
SELECT  dlvr.dlvryStateCd,
        COUNT(*) AS dlvryCnt
  FROM  tb_deliveries AS dlvr
 WHERE  1=1

   AND  dlvr.stoBrcofcId = #{stoBrcofcId}


   AND  dlvr.riderBrcofcId = #{riderBrcofcId}

   AND  date_format(dlvr.dlvryRecvDt, "%Y%m%d%H%i%s")  >=  #{dlvryRecvDtStd}
   AND  date_format(dlvr.dlvryRecvDt, "%Y%m%d%H%i%s")  <=  #{dlvryRecvDtEnd}
 GROUP
    BY  dlvr.dlvryStateCd

SELECT  IFNUlL( COUNT(*), 0 ) AS dlvryCnt
  FROM  tb_deliveries
 WHERE  dlvryNo = #{dlvryNo}

mapper.update:
UPDATE  tb_deliveries
   SET  dlvryStateCd = '02',
        dlvryDsptcDt = date_format(now(), "%Y%m%d%H%i%s"),
        riderId = #{riderId},
        riderBrcofcId = #{riderBrcofcId}
 WHERE  dlvryNo = #{dlvryNo}

UPDATE  tb_deliveries
   SET  dlvryStateCd = '05'
 WHERE  dlvryNo = #{dlvryNo}

mapper.select:
SELECT store.stoId, store.stoBsnsRgnmb, store.stoMtlty, store.stoBizSeCd, store.stoRprsntvNm, store.stoBrdYmd, store.stoCrprtRgnmb, store.stoOpnngYmd, store.stoBsnsPlaceAdres, store.stoHdofcAdres, store.stoBizcnd, store.stoInduty, store.stoTelno, store.stoVrtlAcnt, store.stoSetSeCd, store.stoNightSrchrApplyYn, store.stoNightSrchrStdTm, store.stoNightSrchrEndTm, store.stoNightSrchrAmnt, store.stoLa, store.stoLo, store.stoStateCd FROM tb_stores AS store WHERE store.brcofcId = #{brcofcId}
AND store.stoMtlty LIKE CONCAT('%', #{stoMtlty} ,'%')
ORDER BY store.stoId

SELECT  IFNUlL( COUNT(*), 0 ) AS stoId
  FROM  tb_stores
 WHERE  stoId = #{stoId}

mapper.update:
UPDATE tb_stores
stoBsnsRgnmb = #{stoBsnsRgnmb}, stoMtlty = #{stoMtlty}, stoRprsntvNm = #{stoRprsntvNm}, stoBrdYmd = #{stoBrdYmd}, stoCrprtRgnmb = #{stoCrprtRgnmb}, stoOpnngYmd = #{stoOpnngYmd}, stoBsnsPlaceAdres = #{stoBsnsPlaceAdres}, stoHdofcAdres = #{stoHdofcAdres}, stoBizcnd = #{stoBizcnd}, stoInduty = #{stoInduty}, stoTelno = #{stoTelno}, stoVrtlAcnt = #{stoVrtlAcnt}, stoStateCd = #{stoStateCd}, stoBizSeCd = #{stoBizSeCd}
WHERE stoId = #{stoId}

UPDATE  tb_stores

        stoNightSrchrApplyYn = #{stoNightSrchrApplyYn},
        stoNightSrchrStdTm = #{stoNightSrchrStdTm},
        stoNightSrchrEndTm = #{stoNightSrchrEndTm},
        stoNightSrchrAmnt = #{stoNightSrchrAmnt}

 WHERE  stoId = #{stoId}

UPDATE  tb_store_surcharges

        srchrCn = #{srchrCn},
        srchrAmnt = #{srchrAmnt},
        srchrApplyYn = #{srchrApplyYn}

 WHERE  stoId = #{stoId}
   AND  srchrSeCd = #{srchrSeCd}
